var entry = require('./entry');
var assert = require('assert');

var foo = function() {
  console.log("Foo");
};

var bar = function() {
  console.log("Bar");
};

(function(){

  console.log("Testing get");

  entry.get("foo", foo);

  assert.equal(
    foo,
    entry.getPathCallback("GET","foo"),
    "Function is not storing correctly in get"
  );

  assert.notEqual(
    bar,
    entry.getPathCallback("GET", "foo"),
    "bar and foo should not be equal"
  );

})();

